import math

def isPowerful(n):
    while (n % 2 == 0):
        a = 0
        while (n % 2 == 0):
            n = n // 2
            a = a + 1
        if ( a == 1):
            return False
    for power in range(3, int(math.sqrt(n))+1, 2):
        a = 0
        while (n % power == 0):
            n = n // power
            a = a + 1
        if (a == 1):
            return False
    return (n == 1)

def nthpowerfulnumber(n):
    found = 0
    guess = 0
    while (found <= abs(n)):
        guess += 1
        if(isPowerful(guess)):
            found += 1
    return guess
