def fun_set_kth_digit(n, k, d):
    flag = False
    if(n < 0):
        n = -n
        flag = True
    count = 0
    res = 0
    while(n > 0):
        r = n % 10 
        if count == k:
            res = res + (d * (10 ** count))
        else:
            res = res + (r * (10 ** count))
        count += 1
        n = n // 10
    if count == k:
        res = res + (d * (10 ** count))
    if flag:
        return(-res)
    else:
        return res
